import React, { Component } from 'react'
import styles from './BlogCard.module.css'
class BlogCard extends Component {
  constructor(props) {
    super(props)
  }

  render() {
    const { BlogCardDetails } = this.props.card
    return (
      <div className={styles.BlogCard}>
        <a href='#'>
          <img src={BlogCardDetails.image} alt={BlogCardDetails.title} />
          <div className={styles.BlogCardDesc}>
            <h1>{BlogCardDetails.title}</h1>
            <span className={styles.authorName}>
              {BlogCardDetails.authorName}
            </span>
            | <span>{BlogCardDetails.date}</span>
            <p className={styles.desc}>{BlogCardDetails.desc}</p>
          </div>
        </a>
      </div>
    )
  }
}

export default BlogCard
